import React, { useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Grid,
  TextField,
  Button,
  Divider,
  FormControl,
  FormControlLabel,
  FormGroup,
  Switch,
  InputLabel,
  Select,
  MenuItem,
  Avatar,
  IconButton,
  Tab,
  Tabs,
  Alert,
  CircularProgress
} from '@mui/material';
import PhotoCameraIcon from '@mui/icons-material/PhotoCamera';
import SaveIcon from '@mui/icons-material/Save';
import LockIcon from '@mui/icons-material/Lock';
import api from '../services/api';

const Settings = () => {
  const [tab, setTab] = useState(0);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [profileForm, setProfileForm] = useState({
    name: 'Alex Johnson',
    email: 'alex.j@example.com',
    phone: '+91 9876543210',
    preferredLanguage: 'en',
  });
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });
  const [preferences, setPreferences] = useState({
    emailNotifications: true,
    whatsappNotifications: true,
    saveHistory: true,
    darkMode: false,
  });

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileForm({
      ...profileForm,
      [name]: value,
    });
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordForm({
      ...passwordForm,
      [name]: value,
    });
  };

  const handlePreferenceChange = (e) => {
    const { name, checked } = e.target;
    setPreferences({
      ...preferences,
      [name]: checked,
    });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess(false);
    
    // Simulate API call
    setTimeout(() => {
      console.log('Profile update with:', profileForm);
      setLoading(false);
      setSuccess(true);
      
      // Hide success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    }, 1000);
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      // Handle password mismatch
      return;
    }

    setLoading(true);
    setSuccess(false);
    
    // Simulate API call
    setTimeout(() => {
      console.log('Password update');
      setLoading(false);
      setSuccess(true);
      setPasswordForm({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      });
      
      // Hide success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    }, 1000);
  };

  const handlePreferencesSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess(false);
    
    // Simulate API call
    setTimeout(() => {
      console.log('Preferences update with:', preferences);
      setLoading(false);
      setSuccess(true);
      
      // Hide success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    }, 1000);
  };

  return (
    <Box>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          Settings
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Manage your account settings and preferences
        </Typography>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Paper sx={{ width: '100%' }}>
          <Tabs 
            value={tab} 
            onChange={handleTabChange} 
            variant="fullWidth"
            sx={{ borderBottom: 1, borderColor: 'divider' }}
          >
            <Tab label="Profile" />
            <Tab label="Security" />
            <Tab label="Preferences" />
          </Tabs>
          
          {success && (
            <Alert severity="success" sx={{ mt: 2, mx: 2 }}>
              Changes saved successfully!
            </Alert>
          )}

          {tab === 0 && (
            <Box sx={{ p: 3 }}>
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 4 }}>
                <Avatar 
                  sx={{ 
                    width: 100, 
                    height: 100, 
                    bgcolor: 'primary.main',
                    fontSize: '2rem',
                    mb: 2
                  }}
                >
                  AJ
                </Avatar>
                <IconButton
                  color="primary"
                  aria-label="upload picture"
                  component="label"
                >
                  <input hidden accept="image/*" type="file" />
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <PhotoCameraIcon />
                    <Typography variant="body2">
                      Change Photo
                    </Typography>
                  </Box>
                </IconButton>
              </Box>

              <form onSubmit={handleProfileSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <TextField
                      label="Full Name"
                      variant="outlined"
                      fullWidth
                      name="name"
                      value={profileForm.name}
                      onChange={handleProfileChange}
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      label="Email"
                      variant="outlined"
                      fullWidth
                      type="email"
                      name="email"
                      value={profileForm.email}
                      onChange={handleProfileChange}
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      label="Phone Number"
                      variant="outlined"
                      fullWidth
                      name="phone"
                      value={profileForm.phone}
                      onChange={handleProfileChange}
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth variant="outlined">
                      <InputLabel>Preferred Language</InputLabel>
                      <Select
                        name="preferredLanguage"
                        value={profileForm.preferredLanguage}
                        onChange={handleProfileChange}
                        label="Preferred Language"
                      >
                        <MenuItem value="en">English</MenuItem>
                        <MenuItem value="hi">Hindi</MenuItem>
                        <MenuItem value="ta">Tamil</MenuItem>
                        <MenuItem value="te">Telugu</MenuItem>
                        <MenuItem value="bn">Bengali</MenuItem>
                        <MenuItem value="mr">Marathi</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                      <Button
                        type="submit"
                        variant="contained"
                        startIcon={<SaveIcon />}
                        disabled={loading}
                      >
                        {loading ? <CircularProgress size={24} /> : 'Save Changes'}
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </form>
            </Box>
          )}

          {tab === 1 && (
            <Box sx={{ p: 3 }}>
              <form onSubmit={handlePasswordSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <TextField
                      label="Current Password"
                      variant="outlined"
                      fullWidth
                      type="password"
                      name="currentPassword"
                      value={passwordForm.currentPassword}
                      onChange={handlePasswordChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      label="New Password"
                      variant="outlined"
                      fullWidth
                      type="password"
                      name="newPassword"
                      value={passwordForm.newPassword}
                      onChange={handlePasswordChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <TextField
                      label="Confirm New Password"
                      variant="outlined"
                      fullWidth
                      type="password"
                      name="confirmPassword"
                      value={passwordForm.confirmPassword}
                      onChange={handlePasswordChange}
                      required
                      error={passwordForm.newPassword !== passwordForm.confirmPassword && passwordForm.confirmPassword !== ''}
                      helperText={passwordForm.newPassword !== passwordForm.confirmPassword && passwordForm.confirmPassword !== '' ? "Passwords don't match" : ""}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                      <Button
                        type="submit"
                        variant="contained"
                        startIcon={<LockIcon />}
                        disabled={loading || passwordForm.newPassword !== passwordForm.confirmPassword}
                      >
                        {loading ? <CircularProgress size={24} /> : 'Update Password'}
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </form>
            </Box>
          )}

          {tab === 2 && (
            <Box sx={{ p: 3 }}>
              <form onSubmit={handlePreferencesSubmit}>
                <Typography variant="h6" fontWeight="bold" gutterBottom>
                  Notifications
                </Typography>
                <FormGroup>
                  <FormControlLabel
                    control={<Switch 
                      checked={preferences.emailNotifications} 
                      onChange={handlePreferenceChange}
                      name="emailNotifications"
                    />}
                    label="Email Notifications"
                  />
                  <FormControlLabel
                    control={<Switch 
                      checked={preferences.whatsappNotifications} 
                      onChange={handlePreferenceChange}
                      name="whatsappNotifications"
                    />}
                    label="WhatsApp Notifications"
                  />
                </FormGroup>
                
                <Divider sx={{ my: 3 }} />
                
                <Typography variant="h6" fontWeight="bold" gutterBottom>
                  Privacy
                </Typography>
                <FormGroup>
                  <FormControlLabel
                    control={<Switch 
                      checked={preferences.saveHistory} 
                      onChange={handlePreferenceChange}
                      name="saveHistory"
                    />}
                    label="Save Query History"
                  />
                </FormGroup>
                
                <Divider sx={{ my: 3 }} />
                
                <Typography variant="h6" fontWeight="bold" gutterBottom>
                  Display
                </Typography>
                <FormGroup>
                  <FormControlLabel
                    control={<Switch 
                      checked={preferences.darkMode} 
                      onChange={handlePreferenceChange}
                      name="darkMode"
                    />}
                    label="Dark Mode"
                  />
                </FormGroup>
                
                <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
                  <Button
                    type="submit"
                    variant="contained"
                    startIcon={<SaveIcon />}
                    disabled={loading}
                  >
                    {loading ? <CircularProgress size={24} /> : 'Save Preferences'}
                  </Button>
                </Box>
              </form>
            </Box>
          )}
        </Paper>
      </Box>
    </Box>
  );
};

export default Settings;
