import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Paper, 
  Card, 
  CardContent, 
  CardActionArea,
  CardMedia,
  Button, 
  Divider, 
  List, 
  ListItem,
  ListItemIcon,
  ListItemText,
  Chip,
  useTheme
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import GavelIcon from '@mui/icons-material/Gavel';
import EventNoteIcon from '@mui/icons-material/EventNote';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import FeedbackIcon from '@mui/icons-material/Feedback';
import { getPopularLegalTopics } from '../services/queryService';
import { getUserEngagementMetrics } from '../services/analyticsService';

const Dashboard = () => {
  const [recentQueries, setRecentQueries] = useState([]);
  const [popularTopics, setPopularTopics] = useState([]);
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const theme = useTheme();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDashboardData = async () => {
      setLoading(true);
      try {
        // Get popular legal topics
        const topics = await getPopularLegalTopics();
        setPopularTopics(topics);
        
        // Get user metrics
        const userMetrics = await getUserEngagementMetrics();
        setMetrics(userMetrics);
        
        // Mock recent queries (replace with actual API call)
        setRecentQueries([
          { id: 1, text: "What are the grounds for divorce in India?", date: "2023-09-10" },
          { id: 2, text: "Property inheritance rights for daughters", date: "2023-09-08" },
          { id: 3, text: "Process for filing consumer complaint", date: "2023-09-05" }
        ]);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchDashboardData();
  }, []);
  
  const handleStartNewQuery = () => {
    navigate('/chat');
  };
  
  const handleTopicClick = (topic) => {
    navigate('/chat', { state: { initialQuery: `Tell me about ${topic}` } });
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Welcome Section */}
      <Paper 
        elevation={0} 
        sx={{ 
          p: 3, 
          mb: 4, 
          backgroundImage: 'linear-gradient(120deg, #e0f7fa 0%, #bbdefb 100%)',
          borderRadius: 2
        }}
      >
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={8}>
            <Typography variant="h4" gutterBottom>
              Welcome to Your Legal Assistant
            </Typography>
            <Typography variant="body1" paragraph>
              Access expert legal guidance on Indian laws with AI-powered assistance.
              Get answers with proper citations and contextual information to help with your legal queries.
            </Typography>
            <Button 
              variant="contained" 
              size="large" 
              onClick={handleStartNewQuery}
              startIcon={<HelpOutlineIcon />}
            >
              Ask a Legal Question
            </Button>
          </Grid>
          <Grid item xs={12} md={4}>
            <CardMedia
              component="img"
              image="/assets/legal_illustration.svg"
              alt="Legal assistance illustration"
              sx={{ maxHeight: 200, objectFit: 'contain' }}
            />
          </Grid>
        </Grid>
      </Paper>
      
      {/* Main Dashboard Content */}
      <Grid container spacing={3}>
        {/* Popular Legal Topics */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
              <GavelIcon sx={{ mr: 1 }} /> Popular Legal Topics
            </Typography>
            <Divider sx={{ mb: 2 }} />
            <Grid container spacing={1}>
              {popularTopics.map((topic, index) => (
                <Grid item key={index}>
                  <Chip 
                    label={topic} 
                    color="primary" 
                    variant="outlined" 
                    onClick={() => handleTopicClick(topic)}
                    clickable
                    sx={{ m: 0.5 }}
                  />
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>
        
        {/* Recent Activity */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
              <EventNoteIcon sx={{ mr: 1 }} /> Recent Queries
            </Typography>
            <Divider sx={{ mb: 2 }} />
            {recentQueries.length > 0 ? (
              <List>
                {recentQueries.map(query => (
                  <ListItem 
                    key={query.id}
                    button
                    onClick={() => navigate(`/history/${query.id}`)}
                    sx={{ borderRadius: 1, mb: 1, '&:hover': { bgcolor: 'rgba(0,0,0,0.04)' } }}
                  >
                    <ListItemText 
                      primary={query.text} 
                      secondary={`Asked on ${query.date}`}
                      primaryTypographyProps={{ 
                        variant: 'body2',
                        fontWeight: 'medium', 
                        sx: { 
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap' 
                        } 
                      }}
                    />
                  </ListItem>
                ))}
              </List>
            ) : (
              <Typography color="text.secondary" align="center" sx={{ py: 2 }}>
                No recent queries found
              </Typography>
            )}
            <Button 
              variant="outlined" 
              fullWidth 
              onClick={() => navigate('/history')}
              sx={{ mt: 1 }}
            >
              View All History
            </Button>
          </Paper>
        </Grid>
        
        {/* Legal Resources Cards */}
        <Grid item xs={12}>
          <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
            <TrendingUpIcon sx={{ mr: 1 }} /> Legal Resources
          </Typography>
          <Grid container spacing={2}>
            {[
              { 
                title: "Indian Constitution", 
                desc: "Access the complete Constitution of India with explanations", 
                image: "/assets/constitution.jpg",
                link: "/resources/constitution"
              },
              { 
                title: "Legal Forms", 
                desc: "Download common legal forms and templates", 
                image: "/assets/legal_forms.jpg",
                link: "/resources/forms"
              },
              { 
                title: "Legal Aid", 
                desc: "Find information about legal aid and pro bono services", 
                image: "/assets/legal_aid.jpg",
                link: "/resources/aid"
              }
            ].map((resource, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card elevation={2}>
                  <CardActionArea onClick={() => navigate(resource.link)}>
                    <CardMedia
                      component="img"
                      height="140"
                      image={resource.image || "https://via.placeholder.com/300x140"}
                      alt={resource.title}
                    />
                    <CardContent>
                      <Typography gutterBottom variant="h6" component="div">
                        {resource.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {resource.desc}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>
      
      {/* Feedback Section */}
      <Paper sx={{ p: 3, mt: 4, bgcolor: '#f9f9f9', borderRadius: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <FeedbackIcon sx={{ mr: 2, fontSize: 32, color: theme.palette.primary.main }} />
            <Box>
              <Typography variant="h6">
                Help us improve
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Your feedback makes our Legal Assistant better for everyone
              </Typography>
            </Box>
          </Box>
          <Button variant="outlined" onClick={() => navigate('/feedback')}>
            Give Feedback
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default Dashboard;
