import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Paper, 
  Card, 
  CardContent,
  List,
  ListItem,
  ListItemText,
  Divider,
  Button,
  CircularProgress,
  useTheme,
  Container,
  Alert
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import ChatIcon from '@mui/icons-material/Chat';
import HistoryIcon from '@mui/icons-material/History';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import FeedbackIcon from '@mui/icons-material/Feedback';
import api from '../services/api';

// Demo data - replace with actual API calls in production
const recentQueries = [
  { id: 1, text: "What are the penalties for Section 302 of IPC?", timestamp: "2023-10-25T14:30:00" },
  { id: 2, text: "How to file an RTI application?", timestamp: "2023-10-24T11:15:00" },
  { id: 3, text: "What are my rights if I'm arrested?", timestamp: "2023-10-23T09:45:00" },
];

const popularTopics = [
  { id: 1, name: "Criminal Law", count: 245 },
  { id: 2, name: "Right to Information", count: 187 },
  { id: 3, name: "Labor Laws", count: 156 },
  { id: 4, name: "Family Law", count: 134 },
  { id: 5, name: "Property Law", count: 112 },
];

const Dashboard = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({
    totalQueries: 0,
    answeredQueries: 0,
    averageConfidence: 0,
  });

  useEffect(() => {
    // Check if user is logged in
    const storedUser = localStorage.getItem('user');
    const storedToken = localStorage.getItem('token');

    if (!storedToken || !storedUser) {
      // If not logged in, redirect to login
      navigate('/login', { state: { from: { pathname: '/dashboard' } } });
      return;
    }

    try {
      setUser(JSON.parse(storedUser));
    } catch (err) {
      console.error("Failed to parse user data", err);
      setError("Session data is corrupted. Please log in again.");
      localStorage.removeItem('user');
      localStorage.removeItem('token');
      navigate('/login');
      return;
    }

    // Simulating API call
    const timer = setTimeout(() => {
      try {
        setStats({
          totalQueries: 32,
          answeredQueries: 28,
          averageConfidence: 86.5,
        });
        setLoading(false);
      } catch (err) {
        console.error("Error loading dashboard data", err);
        setError("Failed to load dashboard data. Please try again.");
        setLoading(false);
      }
    }, 1000);

    return () => clearTimeout(timer);
  }, [navigate]);

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
        <Button 
          variant="contained" 
          onClick={() => navigate('/')}
          sx={{ mt: 2 }}
        >
          Return to Home
        </Button>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          Dashboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          {user ? `Welcome back, ${user.name || user.email}` : 'Overview of your legal queries and information'}
        </Typography>
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {/* Stats Cards */}
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent sx={{ textAlign: 'center', py: 3 }}>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  Total Queries
                </Typography>
                <Typography variant="h3" fontWeight="bold" color="primary">
                  {stats.totalQueries}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent sx={{ textAlign: 'center', py: 3 }}>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  Answered Queries
                </Typography>
                <Typography variant="h3" fontWeight="bold" color="primary">
                  {stats.answeredQueries}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent sx={{ textAlign: 'center', py: 3 }}>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  Average Confidence
                </Typography>
                <Typography variant="h3" fontWeight="bold" color="primary">
                  {stats.averageConfidence}%
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          {/* Recent Queries */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3, height: '100%' }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6" fontWeight="bold">
                  Recent Queries
                </Typography>
                <Button 
                  component={Link} 
                  to="/history" 
                  startIcon={<HistoryIcon />}
                  size="small"
                >
                  View All
                </Button>
              </Box>
              <List>
                {recentQueries.map((query, index) => (
                  <React.Fragment key={query.id}>
                    <ListItem alignItems="flex-start">
                      <ListItemText
                        primary={query.text}
                        secondary={new Date(query.timestamp).toLocaleString()}
                      />
                    </ListItem>
                    {index < recentQueries.length - 1 && <Divider component="li" />}
                  </React.Fragment>
                ))}
              </List>
              {recentQueries.length === 0 && (
                <Box sx={{ textAlign: 'center', py: 4 }}>
                  <Typography color="text.secondary">No recent queries</Typography>
                </Box>
              )}
            </Paper>
          </Grid>

          {/* Popular Topics */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3, height: '100%' }}>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                Popular Legal Topics
              </Typography>
              <List>
                {popularTopics.map((topic, index) => (
                  <React.Fragment key={topic.id}>
                    <ListItem sx={{ px: 0 }}>
                      <ListItemText
                        primary={topic.name}
                        secondary={`${topic.count} queries`}
                      />
                    </ListItem>
                    {index < popularTopics.length - 1 && <Divider component="li" />}
                  </React.Fragment>
                ))}
              </List>
            </Paper>
          </Grid>

          {/* Quick Actions */}
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                Quick Actions
              </Typography>
              <Grid container spacing={2} sx={{ mt: 1 }}>
                <Grid item xs={12} sm={6} md={3}>
                  <Button
                    variant="contained"
                    fullWidth
                    size="large"
                    component={Link}
                    to="/chat"
                    startIcon={<ChatIcon />}
                    sx={{ py: 2 }}
                  >
                    New Query
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <Button
                    variant="outlined"
                    fullWidth
                    size="large"
                    component={Link}
                    to="/history"
                    startIcon={<HistoryIcon />}
                    sx={{ py: 2 }}
                  >
                    View History
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <Button
                    variant="outlined"
                    fullWidth
                    size="large"
                    startIcon={<TrendingUpIcon />}
                    sx={{ py: 2 }}
                  >
                    Analytics
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <Button
                    variant="outlined"
                    fullWidth
                    size="large"
                    startIcon={<FeedbackIcon />}
                    sx={{ py: 2 }}
                  >
                    Send Feedback
                  </Button>
                </Grid>
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default Dashboard;
