import React, { useState, useEffect, useRef } from 'react';
import { Box, TextField, Button, Typography, Paper, List, ListItem, Divider } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import api from '../services/api';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async () => {
    if (input.trim() === '') return;

    // Add user message to chat
    const userMessage = { text: input, sender: 'user', timestamp: new Date() };
    setMessages(prevMessages => [...prevMessages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      // Mock response for development/testing if API fails
      let response;
      try {
        // Try the actual API call
        response = await api.sendQuery(input);
      } catch (apiError) {
        console.warn('API call failed, using mock response:', apiError);
        
        // Create a mock response for development/demonstration
        response = {
          response: {
            answer: "Based on Indian legal statutes, I can provide information regarding your query. However, please note that this is a simulated response as the backend service is currently unavailable. In a production environment, you would receive accurate legal information based on Indian laws.",
            citations: [
              { code: "IPC", section: "Demo" },
              { code: "CrPC", section: "Demo" }
            ],
            confidence: 85.5
          }
        };
      }
      
      // Check response structure and handle different formats gracefully
      const answer = response?.response?.answer || 
                    response?.answer || 
                    "I received your query, but couldn't parse the response format.";
      
      const citations = response?.response?.citations || 
                       response?.citations || 
                       [];
      
      const confidence = response?.response?.confidence || 
                        response?.confidence || 
                        null;
      
      // Add bot response to chat
      const botMessage = { 
        text: answer, 
        sender: 'bot', 
        timestamp: new Date(),
        citations: citations,
        confidence: confidence
      };
      
      setMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error processing response:', error);
      
      // Add error message with more details
      const errorMessage = { 
        text: `Sorry, I encountered an error while processing your query. ${error.message || 'Please try again.'}`, 
        sender: 'bot', 
        timestamp: new Date(),
        isError: true
      };
      
      setMessages(prevMessages => [...prevMessages, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%', maxWidth: '800px', margin: '0 auto' }}>
      <Typography variant="h5" component="h2" sx={{ p: 2, textAlign: 'center' }}>
        Legal Assistant for Indian Laws
      </Typography>
      
      <Paper sx={{ flexGrow: 1, mb: 2, p: 2, maxHeight: 'calc(100vh - 200px)', overflowY: 'auto' }}>
        <List>
          {messages.length === 0 ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
              <Typography color="text.secondary">
                Ask any question about Indian laws, sections, or legal procedures.
              </Typography>
            </Box>
          ) : (
            messages.map((message, index) => (
              <React.Fragment key={index}>
                <ListItem sx={{ 
                  display: 'flex', 
                  flexDirection: 'column', 
                  alignItems: message.sender === 'user' ? 'flex-end' : 'flex-start',
                  mb: 1
                }}>
                  <Paper 
                    sx={{ 
                      p: 2, 
                      maxWidth: '80%',
                      bgcolor: message.sender === 'user' ? 'primary.light' : 'background.paper',
                      color: message.sender === 'user' ? 'white' : 'text.primary',
                      borderRadius: message.sender === 'user' ? '20px 20px 5px 20px' : '20px 20px 20px 5px'
                    }}
                  >
                    <Typography variant="body1">{message.text}</Typography>
                    
                    {message.citations && message.citations.length > 0 && (
                      <Box sx={{ mt: 2, fontSize: '0.85rem' }}>
                        <Typography variant="subtitle2">Citations:</Typography>
                        {message.citations.map((cite, i) => (
                          <Typography key={i} variant="body2" sx={{ ml: 1 }}>
                            â€¢ {cite.code} Section {cite.section}
                          </Typography>
                        ))}
                      </Box>
                    )}
                    
                    {message.confidence && (
                      <Typography variant="caption" sx={{ display: 'block', mt: 1, opacity: 0.7 }}>
                        Confidence: {message.confidence.toFixed(1)}%
                      </Typography>
                    )}
                  </Paper>
                  
                  <Typography variant="caption" sx={{ mt: 0.5, opacity: 0.6 }}>
                    {new Date(message.timestamp).toLocaleTimeString()}
                  </Typography>
                </ListItem>
                
                {index < messages.length - 1 && <Divider variant="middle" />}
              </React.Fragment>
            ))
          )}
          <div ref={messagesEndRef} />
        </List>
      </Paper>
      
      <Box sx={{ display: 'flex', mb: 2 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Ask about Indian laws..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          disabled={isLoading}
          multiline
          maxRows={3}
          sx={{ mr: 1 }}
        />
        <Button 
          variant="contained" 
          color="primary"
          onClick={handleSend}
          disabled={isLoading || input.trim() === ''}
          sx={{ minWidth: '100px' }}
        >
          {isLoading ? 'Sending...' : <SendIcon />}
        </Button>
      </Box>
    </Box>
  );
};

export default Chat;
