import React, { useEffect, useRef } from 'react';
import { 
  Box, 
  Button, 
  Container, 
  Grid, 
  Typography, 
  Card, 
  CardContent,
  Paper,
  useTheme,
  useMediaQuery,
  Divider,
  Avatar
} from '@mui/material';
import { Link } from 'react-router-dom';
import ChatIcon from '@mui/icons-material/Chat';
import GavelIcon from '@mui/icons-material/Gavel';
import TranslateIcon from '@mui/icons-material/Translate';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import SearchIcon from '@mui/icons-material/Search';
import SecurityIcon from '@mui/icons-material/Security';

const LandingPage = () => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('md'));
  const heroRef = useRef(null);
  const stepsRef = useRef(null);
  const ctaRef = useRef(null);
  
  useEffect(() => {
    const cards = document.querySelectorAll('.feature-card');
    
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-in');
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.1 });
    
    cards.forEach(card => {
      observer.observe(card);
    });

    const additionalAnimations = [
      { element: heroRef.current, className: 'animate-hero' },
      { element: stepsRef.current, className: 'animate-steps' },
      { element: ctaRef.current, className: 'animate-cta' }
    ];
    
    const additionalObserver = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add(entry.target.dataset.animationClass);
          additionalObserver.unobserve(entry.target);
        }
      });
    }, { threshold: 0.1 });
    
    additionalAnimations.forEach(({element, className}) => {
      if (element) {
        element.dataset.animationClass = className;
        additionalObserver.observe(element);
      }
    });
    
    const heroElement = document.querySelector('.hero-section');
    if (heroElement) {
      createBubbles(heroElement);
    }
    
    return () => {
      cards.forEach(card => {
        observer.unobserve(card);
      });
      additionalObserver.disconnect();
    };
  }, []);
  
  // Function to create floating bubbles
  const createBubbles = (parent) => {
    const bubbleCount = 6;
    const bubbleContainer = document.createElement('div');
    bubbleContainer.className = 'bubble-container';
    
    for (let i = 0; i < bubbleCount; i++) {
      const bubble = document.createElement('div');
      bubble.className = 'bubble';
      bubble.style.setProperty('--size', `${Math.random() * 80 + 40}px`);
      bubble.style.setProperty('--left', `${Math.random() * 100}%`);
      bubble.style.setProperty('--delay', `${Math.random() * 4}s`);
      bubble.style.setProperty('--duration', `${Math.random() * 10 + 10}s`);
      bubble.style.setProperty('--opacity', `${Math.random() * 0.3 + 0.1}`);
      
      bubbleContainer.appendChild(bubble);
    }
    
    parent.appendChild(bubbleContainer);
  };
  
  return (
    <Box>
      {/* Hero Section */}
      <Box
        ref={heroRef}
        className="hero-section"
        sx={{
          background: 'linear-gradient(45deg, #3a0ca3 0%, #4361ee 100%)',
          color: 'white',
          py: 10,
          borderRadius: { xs: '0 0 30px 30px', md: '0 0 50px 50px' },
          boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
          position: 'relative',
          overflow: 'hidden',
        }}
      >
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography 
                variant="h2" 
                fontWeight="bold"
                sx={{ 
                  mb: 2,
                  fontSize: { xs: '2.5rem', md: '3.5rem' }
                }}
              >
                RAGify India
              </Typography>
              <Typography 
                variant="h4" 
                gutterBottom
                sx={{ 
                  mb: 3,
                  fontSize: { xs: '1.5rem', md: '2rem' }
                }}
              >
                Your AI Legal Assistant for Indian Laws
              </Typography>
              <Typography variant="body1" paragraph sx={{ mb: 4, fontSize: '1.1rem' }}>
                Navigate complex Indian legal codes with our intelligent assistant powered by 
                Retrieval-Augmented Generation technology. Get accurate, contextual legal information in seconds.
              </Typography>
              
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <Button 
                  variant="contained" 
                  color="secondary" 
                  size="large"
                  component={Link}
                  to="/chat"
                  startIcon={<ChatIcon />}
                  sx={{ 
                    borderRadius: '50px', 
                    px: 4, 
                    py: 1.5,
                    fontWeight: 'bold',
                    background: 'white',
                    color: '#3a0ca3',
                    '&:hover': {
                      background: 'rgba(255,255,255,0.9)',
                    }
                  }}
                >
                  Start Chat
                </Button>
                <Button 
                  variant="outlined" 
                  size="large"
                  startIcon={<WhatsAppIcon />}
                  onClick={() => window.open('https://wa.me/919012345678?text=Hi%20RAGify%20India!%20I%20need%20legal%20assistance.', '_blank')}
                  sx={{ 
                    borderRadius: '50px', 
                    px: 4, 
                    py: 1.5,
                    fontWeight: 'bold',
                    borderColor: 'white',
                    color: 'white',
                    '&:hover': {
                      borderColor: 'white',
                      background: 'rgba(255,255,255,0.1)',
                    }
                  }}
                >
                  Connect to WhatsApp
                </Button>
              </Box>
            </Grid>
            <Grid item xs={12} md={6} sx={{ display: { xs: 'none', md: 'block' } }}>
              <Box 
                component="img"
                src="/images/001.jpg"
                alt="Legal Assistant Illustration"
                sx={{ 
                  width: '100%',
                  maxHeight: '400px',
                  objectFit: 'contain',
                  borderRadius: '20px',
                  boxShadow: '0 10px 30px rgba(0,0,0,0.15)',
                }}
              />
            </Grid>
          </Grid>
        </Container>
        
        {/* Decorative elements */}
        <Box 
          sx={{ 
            position: 'absolute',
            top: '10%',
            right: '5%',
            width: '150px',
            height: '150px',
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.1)',
            zIndex: 0
          }}
        />
        <Box 
          sx={{ 
            position: 'absolute',
            bottom: '15%',
            left: '10%',
            width: '100px',
            height: '100px',
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.1)',
            zIndex: 0
          }}
        />
      </Box>

      {/* Features Section - Enhanced */}
      <Container maxWidth="lg" sx={{ py: 10 }}>
        <Box textAlign="center" mb={8}>
          <Typography 
            variant="h3" 
            fontWeight="bold" 
            gutterBottom
            sx={{ 
              fontSize: { xs: '2rem', md: '2.5rem' },
              position: 'relative',
              display: 'inline-block',
              '&::after': {
                content: '""',
                position: 'absolute',
                width: '60px',
                height: '4px',
                backgroundColor: 'primary.main',
                bottom: '-12px',
                left: 'calc(50% - 30px)',
                borderRadius: '2px'
              }
            }}
          >
            Powerful Legal Assistance
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ maxWidth: '800px', mx: 'auto', mt: 4 }}>
            Our AI-powered system combines cutting-edge technology with comprehensive Indian legal data
          </Typography>
        </Box>
        
        <Grid container spacing={4}>
          {[
            { 
              icon: <GavelIcon fontSize="large" />, 
              color: '#4361ee',
              title: 'Comprehensive Legal Coverage', 
              description: 'Access information on IPC, RTI, Labor laws, Family laws and more from official sources' 
            },
            { 
              icon: <SearchIcon fontSize="large" />, 
              color: '#3a0ca3',
              title: 'Intelligent Retrieval', 
              description: 'Our BERT-based retrieval system finds the most relevant legal information for your queries' 
            },
            { 
              icon: <TranslateIcon fontSize="large" />, 
              color: '#4895ef',
              title: 'Multi-Lingual Support', 
              description: 'Get legal assistance in multiple Indian languages including Hindi, Bengali, Tamil and more' 
            },
            { 
              icon: <SecurityIcon fontSize="large" />, 
              color: '#560bad',
              title: 'Accurate & Reliable', 
              description: 'Citations from official legal sources ensure the information you receive is trustworthy' 
            },
            { 
              icon: <WhatsAppIcon fontSize="large" />, 
              color: '#38b000',
              title: 'WhatsApp Integration', 
              description: 'Access legal assistance directly through WhatsApp for maximum convenience' 
            },
            { 
              icon: <ChatIcon fontSize="large" />, 
              color: '#f72585',
              title: 'Conversational Interface', 
              description: 'Ask questions in natural language and get clear, contextual answers' 
            },
          ].map((feature, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card 
                className="feature-card"
                elevation={0} 
                sx={{ 
                  height: '100%', 
                  borderRadius: '16px',
                  position: 'relative',
                  overflow: 'hidden',
                  transition: 'all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
                  border: '1px solid rgba(0,0,0,0.05)',
                  opacity: 0,
                  transform: 'translateY(20px)',
                  '&.animate-in': {
                    opacity: 1,
                    transform: 'translateY(0)'
                  },
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '4px',
                    background: feature.color,
                    zIndex: 1
                  },
                  '&:hover': {
                    transform: 'translateY(-10px)',
                    boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
                  }
                }}
              >
                <CardContent sx={{ p: 4, textAlign: 'center' }}>
                  <Avatar 
                    sx={{ 
                      width: 80, 
                      height: 80, 
                      backgroundColor: `${feature.color}15`, 
                      color: feature.color,
                      mb: 3,
                      mx: 'auto',
                      transform: 'scale(1)',
                      transition: 'transform 0.3s ease',
                      '& .MuiSvgIcon-root': {
                        fontSize: '2.2rem'
                      },
                      '.feature-card:hover &': {
                        transform: 'scale(1.1)',
                      }
                    }}
                  >
                    {feature.icon}
                  </Avatar>
                  <Typography 
                    variant="h6" 
                    fontWeight="bold" 
                    gutterBottom
                    sx={{ mb: 2 }}
                  >
                    {feature.title}
                  </Typography>
                  <Typography 
                    variant="body2" 
                    color="text.secondary"
                    sx={{ lineHeight: 1.7 }}
                  >
                    {feature.description}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* How it Works Section */}
      <Box sx={{ bgcolor: 'rgba(67, 97, 238, 0.05)', py: 10 }} ref={stepsRef}>
        <Container maxWidth="lg">
          <Box textAlign="center" mb={8}>
            <Typography 
              variant="h3" 
              fontWeight="bold" 
              gutterBottom
              sx={{ fontSize: { xs: '2rem', md: '2.5rem' } }}
            >
              How It Works
            </Typography>
            <Typography variant="h6" color="text.secondary" sx={{ maxWidth: '800px', mx: 'auto' }}>
              Powered by Retrieval-Augmented Generation (RAG) technology
            </Typography>
          </Box>
          
          <Box sx={{ px: { md: 10 } }}>
            <Grid container spacing={4}>
              {[
                { step: 1, title: 'Ask Your Question', description: 'Submit your legal query in natural language through our chat interface or WhatsApp' },
                { step: 2, title: 'AI Analyzes Query', description: 'Our system identifies the relevant legal domains and topics in your query' },
                { step: 3, title: 'Retrieval of Legal Data', description: 'The BERT-based retriever finds the most relevant sections from Indian legal codes' },
                { step: 4, title: 'Generation of Response', description: 'Our language model generates a clear, contextual answer based on the retrieved information' },
                { step: 5, title: 'Citation & Verification', description: 'All responses include citations to official legal sources that you can verify' },
              ].map((step, index) => (
                <Grid item xs={12} key={index}>
                  <Paper 
                    elevation={0} 
                    sx={{ 
                      p: 4, 
                      borderRadius: '16px',
                      display: 'flex',
                      flexDirection: { xs: 'column', sm: 'row' },
                      alignItems: { xs: 'flex-start', sm: 'center' },
                      gap: 3
                    }}
                  >
                    <Box 
                      sx={{ 
                        width: 60, 
                        height: 60, 
                        borderRadius: '50%', 
                        bgcolor: 'primary.main', 
                        color: 'white',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontWeight: 'bold',
                        fontSize: '1.5rem',
                        flexShrink: 0
                      }}
                    >
                      {step.step}
                    </Box>
                    <Box>
                      <Typography variant="h6" fontWeight="bold">{step.title}</Typography>
                      <Typography variant="body1" color="text.secondary">{step.description}</Typography>
                    </Box>
                  </Paper>
                  {index < 4 && (
                    <Box 
                      sx={{ 
                        display: 'flex', 
                        justifyContent: 'center', 
                        py: 1
                      }}
                    >
                      <Box sx={{ 
                        height: 40, 
                        width: 2, 
                        bgcolor: 'primary.light',
                        opacity: 0.5
                      }} />
                    </Box>
                  )}
                </Grid>
              ))}
            </Grid>
          </Box>
        </Container>
      </Box>

      {/* Call to Action */}
      <Box 
        ref={ctaRef}
        sx={{ 
          py: 10, 
          background: 'linear-gradient(45deg, #3a0ca3 0%, #4361ee 100%)',
          color: 'white',
          textAlign: 'center',
          position: 'relative',
          overflow: 'hidden'
        }}
        className="cta-section"
      >
        <Container maxWidth="md">
          <Typography 
            variant="h3" 
            fontWeight="bold" 
            gutterBottom
            sx={{ fontSize: { xs: '2rem', md: '2.5rem' } }}
          >
            Start Using RAGify India Today
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, opacity: 0.9 }}>
            Get accurate legal information in seconds
          </Typography>
          
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 3, flexWrap: 'wrap' }}>
            <Button 
              variant="contained" 
              size="large"
              component={Link}
              to="/chat"
              startIcon={<ChatIcon />}
              sx={{ 
                borderRadius: '50px', 
                px: 4, 
                py: 1.5,
                fontWeight: 'bold',
                background: 'white',
                color: '#3a0ca3',
                '&:hover': {
                  background: 'rgba(255,255,255,0.9)',
                }
              }}
            >
              Start Chat
            </Button>
            <Button 
              variant="outlined" 
              size="large"
              startIcon={<WhatsAppIcon />}
              sx={{ 
                borderRadius: '50px', 
                px: 4, 
                py: 1.5,
                fontWeight: 'bold',
                borderColor: 'white',
                color: 'white',
                '&:hover': {
                  borderColor: 'white',
                  background: 'rgba(255,255,255,0.1)',
                }
              }}
            >
              Connect WhatsApp
            </Button>
          </Box>
        </Container>
      </Box>

      {/* Footer */}
      <Box 
        sx={{ 
          py: 5,
          bgcolor: 'background.paper'
        }}
      >
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            <Grid item xs={12} md={4}>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                RAGify India
              </Typography>
              <Typography variant="body2" color="text.secondary">
                AI-Powered Legal Assistant for Navigating Indian Laws
              </Typography>
            </Grid>
            <Grid item xs={12} md={8}>
              <Grid container spacing={2}>
                <Grid item xs={6} sm={4}>
                  <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                    Legal Topics
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>IPC</Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>RTI</Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>Labor Laws</Typography>
                </Grid>
                <Grid item xs={6} sm={4}>
                  <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                    Resources
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>Documentation</Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>FAQ</Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>About</Typography>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                    Contact
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>Support</Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>Feedback</Typography>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Divider sx={{ my: 3 }} />
          <Typography variant="body2" color="text.secondary" align="center">
            © {new Date().getFullYear()} RAGify India. All rights reserved.
          </Typography>
        </Container>
      </Box>

      {/* Add styles directly into the component */}
      <style jsx="true">{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .feature-card {
          animation-duration: 0.6s;
          animation-fill-mode: both;
          animation-timing-function: cubic-bezier(0.175, 0.885, 0.32, 1.275);
        }
        .feature-card.animate-in {
          animation-name: fadeInUp;
        }
        .feature-card:nth-child(1) { animation-delay: 0.1s; }
        .feature-card:nth-child(2) { animation-delay: 0.2s; }
        .feature-card:nth-child(3) { animation-delay: 0.3s; }
        .feature-card:nth-child(4) { animation-delay: 0.4s; }
        .feature-card:nth-child(5) { animation-delay: 0.5s; }
        .feature-card:nth-child(6) { animation-delay: 0.6s; }
        
        .animate-hero .hero-title {
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.1s;
        }
        .animate-hero .hero-subtitle {
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.3s;
        }
        .animate-hero .hero-description {
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.5s;
        }
        .animate-hero .hero-buttons {
          opacity: 0;
          transform: translateY(30px);
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.7s;
        }
        .animate-hero .hero-image {
          animation: fadeInRight 1s forwards;
          animation-delay: 0.6s;
        }
        
        .floating-circle {
          animation: float 10s infinite ease-in-out;
        }
        .circle-1 {
          animation-delay: 0s;
        }
        .circle-2 {
          animation-delay: 2s;
        }
        
        .animate-steps .section-title {
          animation: fadeInUp 0.8s forwards;
        }
        .animate-steps .step-item {
          animation: fadeInUp 0.8s forwards;
        }
        .animate-steps .connector {
          opacity: 0;
          animation: fadeIn 1s forwards;
          animation-delay: 1s;
        }
        
        .animate-cta .cta-title {
          animation: fadeInUp 0.8s forwards;
        }
        .animate-cta .cta-subtitle {
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.2s;
        }
        .animate-cta .cta-buttons {
          opacity: 0;
          transform: translateY(30px);
          animation: fadeInUp 0.8s forwards;
          animation-delay: 0.4s;
        }
        
        .bubble-container {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          pointer-events: none;
        }
        .bubble {
          position: absolute;
          width: var(--size);
          height: var(--size);
          border-radius: 50%;
          background-color: rgba(255, 255, 255, var(--opacity));
          bottom: -50px;
          left: var(--left);
          animation: rise var(--duration) ease-in infinite;
          animation-delay: var(--delay);
        }
        
        .floating-button {
          animation: float-subtle 4s ease-in-out infinite;
        }
        
        .floating-shape {
          animation-name: float;
          animation-duration: 10s;
          animation-timing-function: ease-in-out;
          animation-iteration-count: infinite;
        }
        
        @keyframes fadeInRight {
          from {
            opacity: 0;
            transform: translateX(30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
        
        @keyframes float {
          0% {
            transform: translateY(0px) translateX(0px);
          }
          50% {
            transform: translateY(-20px) translateX(10px);
          }
          100% {
            transform: translateY(0px) translateX(0px);
          }
        }
        
        @keyframes float-subtle {
          0% {
            transform: translateY(0px);
          }
          50% {
            transform: translateY(-5px);
          }
          100% {
            transform: translateY(0px);
          }
        }
        
        @keyframes pulse {
          0% {
            box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
          }
          70% {
            box-shadow: 0 0 0 10px rgba(255, 255, 255, 0);
          }
          100% {
            box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
          }
        }
        
        @keyframes rise {
          from {
            bottom: -100px;
            opacity: var(--opacity);
          }
          50% {
            opacity: var(--opacity);
          }
          to {
            bottom: 100%;
            opacity: 0;
          }
        }
      `}</style>
    </Box>
  );
};

export default LandingPage;
