import React, { useEffect, useRef } from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Button, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Divider,
  Paper,
  Stack,
  useMediaQuery,
  useTheme
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import GavelIcon from '@mui/icons-material/Gavel';
import TranslateIcon from '@mui/icons-material/Translate';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';
import ShieldIcon from '@mui/icons-material/Shield';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';

const LandingPage = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const featuresRef = useRef(null);
  const howItWorksRef = useRef(null);
  const testimonialsRef = useRef(null);
  const ctaRef = useRef(null);

  useEffect(() => {
    // Add animation observer
    const observers = [];
    const animatedElements = [
      { ref: featuresRef, className: 'animate-features' },
      { ref: howItWorksRef, className: 'animate-how-it-works' },
      { ref: testimonialsRef, className: 'animate-testimonials' },
      { ref: ctaRef, className: 'animate-cta' }
    ];

    animatedElements.forEach(({ ref, className }) => {
      if (ref.current) {
        const observer = new IntersectionObserver(
          (entries) => {
            entries.forEach((entry) => {
              if (entry.isIntersecting) {
                entry.target.classList.add(className);
                observer.unobserve(entry.target);
              }
            });
          },
          { threshold: 0.1 }
        );

        observer.observe(ref.current);
        observers.push(observer);
      }
    });

    // Animate hero section immediately
    document.querySelector('.hero-content')?.classList.add('animate-hero');

    return () => {
      observers.forEach(observer => {
        observer.disconnect();
      });
    };
  }, []);

  const features = [
    {
      title: "AI-Powered Legal Assistance",
      description: "Get instant answers to your legal questions with accurate citations to Indian laws and precedents.",
      icon: <GavelIcon fontSize="large" />
    },
    {
      title: "Multi-Lingual Support",
      description: "Ask questions in 10+ Indian languages and receive answers in your preferred language.",
      icon: <TranslateIcon fontSize="large" />
    },
    {
      title: "Legal Citations & Context",
      description: "All responses include proper citations to legal statutes, cases, and official sources.",
      icon: <MenuBookIcon fontSize="large" />
    },
    {
      title: "Privacy & Security",
      description: "Your data is encrypted and your consultations remain confidential and secure.",
      icon: <ShieldIcon fontSize="large" />
    }
  ];

  const testimonials = [
    {
      quote: "This legal assistant helped me understand the property inheritance laws quickly without expensive lawyer consultation.",
      author: "Rajesh Kumar, Delhi"
    },
    {
      quote: "Being able to ask questions in Tamil made a huge difference. The citations helped me prepare for my court case.",
      author: "Priya Subramaniam, Chennai"
    },
    {
      quote: "As a law student, this tool has been invaluable for research and understanding complex legal concepts.",
      author: "Arjun Mehta, Law Student"
    }
  ];

  return (
    <Box>
      {/* Hero Section with Animation */}
      <Box sx={{ 
        bgcolor: 'primary.main', 
        color: 'white', 
        pt: 12, 
        pb: 15,
        position: 'relative',
        overflow: 'hidden',
        backgroundImage: 'linear-gradient(135deg, #4361ee 0%, #3a0ca3 100%)'
      }}>
        <Box 
          sx={{ 
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            opacity: 0.1,
            backgroundImage: 'url(/assets/pattern.svg)',
            backgroundRepeat: 'repeat',
            backgroundSize: '500px',
          }}
        />
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={7} className="hero-content hero-text">
              <Typography 
                variant="h2" 
                component="h1" 
                gutterBottom
                className="animated-text"
                sx={{ 
                  fontWeight: 700,
                  fontSize: { xs: '2.5rem', md: '3.5rem' },
                  position: 'relative',
                  opacity: 0,
                  transform: 'translateY(20px)',
                }}
              >
                Indian Legal Assistant <AutoAwesomeIcon sx={{ fontSize: '1.5rem', ml: 1 }} className="animated-icon" />
              </Typography>
              <Typography 
                variant="h5" 
                component="p" 
                gutterBottom 
                className="animated-text"
                sx={{ 
                  mb: 4, 
                  fontWeight: 400, 
                  maxWidth: 600,
                  opacity: 0,
                  transform: 'translateY(20px)',
                }}
              >
                Your AI-powered guide to navigating Indian laws and legal information with accurate, cited, and multilingual support.
              </Typography>
              <Stack 
                direction={{ xs: 'column', sm: 'row' }} 
                spacing={2}
                className="animated-buttons"
                sx={{ 
                  opacity: 0,
                  transform: 'translateY(20px)',
                }}
              >
                <Button 
                  component={RouterLink} 
                  to="/register" 
                  variant="contained" 
                  color="secondary" 
                  size="large"
                  className="pulse-animation"
                >
                  Get Started Free
                </Button>
                <Button 
                  component={RouterLink} 
                  to="/chat" 
                  variant="outlined" 
                  color="inherit" 
                  size="large"
                >
                  Try Demo
                </Button>
              </Stack>
            </Grid>
            <Grid item xs={12} md={5} className="hero-content hero-image" sx={{ opacity: 0 }}>
              <Box 
                component="img"
                src="/assets/legal_hero.svg"
                alt="Legal AI Assistant"
                sx={{ 
                  width: '100%',
                  maxWidth: 500,
                  display: { xs: 'none', md: 'block' },
                  filter: 'drop-shadow(0px 10px 20px rgba(0,0,0,0.25))'
                }}
              />
            </Grid>
          </Grid>
        </Container>
        
        {/* Wave separator */}
        <Box
          sx={{
            position: 'absolute',
            bottom: -1,
            left: 0,
            width: '100%',
            overflow: 'hidden',
            lineHeight: 0,
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 1200 120"
            preserveAspectRatio="none"
            style={{
              position: 'relative',
              display: 'block',
              width: 'calc(100% + 1.3px)',
              height: 60,
            }}
            fill="#ffffff"
            className="wave-animation"
          >
            <path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" />
          </svg>
        </Box>
      </Box>

      {/* Features Section with Animation */}
      <Container maxWidth="lg" sx={{ py: 8 }} ref={featuresRef}>
        <Box textAlign="center" sx={{ mb: 6 }} className="section-heading">
          <Typography variant="h3" component="h2" gutterBottom sx={{ fontWeight: 700 }}>
            Key Features
          </Typography>
          <Typography variant="h6" color="text.secondary" sx={{ maxWidth: 700, mx: 'auto' }}>
            Our AI-powered legal assistant brings Indian law expertise to your fingertips
          </Typography>
        </Box>
        
        <Grid container spacing={4}>
          {features.map((feature, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Paper 
                elevation={2} 
                className="feature-card"
                sx={{ 
                  p: 3, 
                  height: '100%', 
                  display: 'flex', 
                  flexDirection: 'column',
                  alignItems: 'center',
                  textAlign: 'center',
                  borderRadius: 2,
                  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'translateY(-8px)',
                    boxShadow: '0 10px 30px rgba(0,0,0,0.1)'
                  },
                  opacity: 0,
                  transform: 'translateY(40px)',
                }}
                style={{ 
                  transitionDelay: `${index * 100}ms`
                }}
              >
                <Box 
                  sx={{ 
                    color: 'primary.main', 
                    display: 'flex', 
                    p: 1,
                    mb: 2,
                    bgcolor: 'primary.light',
                    opacity: 0.8,
                    borderRadius: '50%',
                    transition: 'transform 0.3s ease-in-out',
                    '.feature-card:hover &': {
                      transform: 'scale(1.1) rotate(5deg)'
                    }
                  }}
                >
                  {feature.icon}
                </Box>
                <Typography variant="h6" component="h3" gutterBottom sx={{ fontWeight: 600 }}>
                  {feature.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {feature.description}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Container>
      
      {/* How It Works Section with Animation */}
      <Box sx={{ bgcolor: 'grey.100', py: 8 }} ref={howItWorksRef}>
        <Container maxWidth="lg">
          <Typography 
            variant="h3" 
            component="h2" 
            gutterBottom 
            textAlign="center"
            sx={{ fontWeight: 700, mb: 6 }}
            className="section-heading"
          >
            How It Works
          </Typography>
          
          <Grid container spacing={isMobile ? 2 : 4} alignItems="center">
            <Grid item xs={12} md={6} className="slide-in-left">
              <Paper
                elevation={0}
                sx={{
                  p: 3,
                  height: '100%',
                  borderRadius: 2,
                  bgcolor: 'background.paper',
                  border: '1px solid',
                  borderColor: 'divider',
                  opacity: 0,
                  transform: 'translateX(-40px)',
                }}
              >
                <Box
                  component="img"
                  src="/assets/demo_chat.png"
                  alt="Chat Demo"
                  sx={{
                    width: '100%',
                    borderRadius: 1,
                    border: '1px solid',
                    borderColor: 'divider',
                  }}
                />
              </Paper>
            </Grid>
            
            <Grid item xs={12} md={6}>
              <Stack spacing={3}>
                {[
                  {
                    step: "1",
                    title: "Ask Your Question",
                    desc: "Type your legal query in any Indian language"
                  },
                  {
                    step: "2",
                    title: "Get Instant Analysis",
                    desc: "Our AI processes your query and searches relevant Indian legal codes"
                  },
                  {
                    step: "3",
                    title: "Review Cited Results",
                    desc: "Receive answers with proper citations to statutes, cases, and legal precedents"
                  },
                  {
                    step: "4",
                    title: "Explore Further",
                    desc: "Click citations to view full legal texts and related information"
                  }
                ].map((item, i) => (
                  <Box 
                    key={i} 
                    className="step-item"
                    sx={{ 
                      display: 'flex', 
                      alignItems: 'flex-start',
                      gap: 2,
                      opacity: 0,
                      transform: 'translateX(40px)',
                      transition: 'all 0.6s ease',
                    }}
                    style={{ 
                      transitionDelay: `${i * 100 + 200}ms` 
                    }}
                  >
                    <Box 
                      sx={{ 
                        width: 36, 
                        height: 36, 
                        borderRadius: '50%', 
                        bgcolor: 'primary.main',
                        color: 'white',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontWeight: 'bold',
                        flexShrink: 0,
                        transition: 'all 0.3s ease',
                        '.step-item:hover &': {
                          transform: 'scale(1.1)',
                          boxShadow: '0 0 15px rgba(67, 97, 238, 0.5)'
                        }
                      }}
                    >
                      {item.step}
                    </Box>
                    <Box>
                      <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                        {item.title}
                      </Typography>
                      <Typography variant="body1" color="text.secondary">
                        {item.desc}
                      </Typography>
                    </Box>
                  </Box>
                ))}
              </Stack>
            </Grid>
          </Grid>
        </Container>
      </Box>
      
      {/* Testimonials Section with Animation */}
      <Container maxWidth="lg" sx={{ py: 8 }} ref={testimonialsRef}>
        <Typography 
          variant="h3" 
          component="h2" 
          gutterBottom 
          textAlign="center"
          sx={{ fontWeight: 700, mb: 2 }}
          className="section-heading"
        >
          User Testimonials
        </Typography>
        <Typography 
          variant="h6" 
          color="text.secondary" 
          textAlign="center" 
          sx={{ maxWidth: 700, mx: 'auto', mb: 6 }}
        >
          See how our AI legal assistant is helping people across India
        </Typography>
        
        <Grid container spacing={4}>
          {testimonials.map((testimonial, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Card 
                className="testimonial-card"
                sx={{ 
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  p: 1,
                  opacity: 0,
                  transform: 'translateY(40px)',
                  transition: 'all 0.6s ease',
                }}
                style={{ 
                  transitionDelay: `${index * 150}ms` 
                }}
                elevation={1}
              >
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography 
                    variant="body1" 
                    paragraph
                    sx={{ 
                      position: 'relative',
                      '&::before': {
                        content: '"""',
                        fontSize: '3rem',
                        position: 'absolute',
                        top: -20,
                        left: -10,
                        opacity: 0.2,
                        color: 'primary.main'
                      }
                    }}
                  >
                    {testimonial.quote}
                  </Typography>
                  <Box sx={{ mt: 'auto' }}>
                    <Divider sx={{ my: 2 }} />
                    <Typography variant="subtitle2" color="text.primary">
                      {testimonial.author}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      
      {/* CTA Section with Animation */}
      <Box 
        ref={ctaRef}
        sx={{ 
          bgcolor: 'primary.dark', 
          color: 'white', 
          py: 8,
          position: 'relative',
          overflow: 'hidden',
          backgroundImage: 'linear-gradient(135deg, #3a0ca3 0%, #4361ee 100%)'
        }}
        className="cta-section"
      >
        <Box 
          sx={{ 
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            opacity: 0.1,
            backgroundImage: 'url(/assets/pattern.svg)',
            backgroundRepeat: 'repeat',
            backgroundSize: '400px',
          }}
        />
        <Container maxWidth="md">
          <Box textAlign="center" className="cta-content" sx={{ opacity: 0, transform: 'scale(0.95)' }}>
            <Typography variant="h3" gutterBottom sx={{ fontWeight: 700 }}>
              Start Getting Legal Answers Today
            </Typography>
            <Typography variant="h6" sx={{ mb: 4, fontWeight: 400 }}>
              Join thousands of users who are navigating India's legal system with confidence
            </Typography>
            <Stack 
              direction={{ xs: 'column', sm: 'row' }} 
              spacing={2} 
              justifyContent="center"
            >
              <Button 
                component={RouterLink} 
                to="/register" 
                variant="contained" 
                color="secondary" 
                size="large"
                className="pulse-animation"
              >
                Sign Up for Free
              </Button>
              <Button 
                component={RouterLink} 
                to="/login" 
                variant="outlined" 
                color="inherit" 
                size="large"
              >
                Log In
              </Button>
            </Stack>
          </Box>
        </Container>
      </Box>
      
      {/* Footer */}
      <Box sx={{ bgcolor: 'background.paper', py: 4 }}>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            <Grid item xs={12} md={4}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <SupportAgentIcon sx={{ mr: 1 }} />
                <Typography variant="h6" component="div">
                  Indian Legal Assistant
                </Typography>
              </Box>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                AI-powered legal assistance for Indian laws and regulations.
                Providing accessible legal information to everyone.
              </Typography>
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Typography variant="subtitle1" gutterBottom>Quick Links</Typography>
              <Stack spacing={1}>
                {["Home", "About", "Features", "Pricing", "Contact"].map((link) => (
                  <Button 
                    key={link} 
                    component={RouterLink} 
                    to={link === "Home" ? "/" : `/${link.toLowerCase()}`}
                    color="inherit"
                    sx={{ justifyContent: 'flex-start', textTransform: 'none' }}
                  >
                    {link}
                  </Button>
                ))}
              </Stack>
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Typography variant="subtitle1" gutterBottom>Legal Categories</Typography>
              <Stack spacing={1}>
                {["Family Law", "Property Law", "Criminal Law", "Consumer Protection", "Corporate Law"].map((category) => (
                  <Button 
                    key={category} 
                    component={RouterLink} 
                    to={`/topics/${category.toLowerCase().replace(" ", "-")}`}
                    color="inherit"
                    sx={{ justifyContent: 'flex-start', textTransform: 'none' }}
                  >
                    {category}
                  </Button>
                ))}
              </Stack>
            </Grid>
          </Grid>
          <Divider sx={{ my: 3 }} />
          <Box sx={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap' }}>
            <Typography variant="body2" color="text.secondary">
              © {new Date().getFullYear()} Indian Legal Assistant. All rights reserved.
            </Typography>
            <Box>
              <Button size="small" color="inherit">Privacy Policy</Button>
              <Button size="small" color="inherit">Terms of Service</Button>
            </Box>
          </Box>
        </Container>
      </Box>

      {/* Add styles directly into the component with style tag */}
      <style jsx="true">{`
        /* Hero animations */
        .animate-hero .hero-text .animated-text {
          animation: fadeInUp 0.7s forwards;
        }
        .animate-hero .hero-text .animated-text:nth-child(1) {
          animation-delay: 0.2s;
        }
        .animate-hero .hero-text .animated-text:nth-child(2) {
          animation-delay: 0.4s;
        }
        .animate-hero .hero-text .animated-buttons {
          animation: fadeInUp 0.7s forwards;
          animation-delay: 0.6s;
        }
        .animate-hero .hero-image {
          animation: fadeInRight 0.9s forwards;
          animation-delay: 0.5s;
        }
        .animated-icon {
          animation: pulse 2s infinite;
        }
        .wave-animation {
          animation: wave 8s linear infinite;
        }
        
        /* Section animations */
        .animate-features .section-heading {
          animation: fadeInUp 0.6s forwards;
        }
        .animate-features .feature-card {
          animation: fadeInUp 0.6s forwards;
        }
        
        .animate-how-it-works .section-heading {
          animation: fadeIn 0.6s forwards;
        }
        .animate-how-it-works .slide-in-left {
          animation: slideInLeft 0.7s forwards;
        }
        .animate-how-it-works .step-item {
          animation: slideInRight 0.7s forwards;
        }
        
        .animate-testimonials .section-heading {
          animation: fadeIn 0.6s forwards;
        }
        .animate-testimonials .testimonial-card {
          animation: fadeInUp 0.6s forwards;
        }
        
        .animate-cta .cta-content {
          animation: scaleIn 0.7s forwards;
        }
        
        /* Button animation */
        .pulse-animation {
          animation: buttonPulse 2s infinite;
        }

        /* Keyframes */
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeInRight {
          from {
            opacity: 0;
            transform: translateX(40px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        
        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
        
        @keyframes slideInLeft {
          from {
            opacity: 0;
            transform: translateX(-40px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        
        @keyframes slideInRight {
          from {
            opacity: 0;
            transform: translateX(40px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        
        @keyframes scaleIn {
          from {
            opacity: 0;
            transform: scale(0.95);
          }
          to {
            opacity: 1;
            transform: scale(1);
          }
        }
        
        @keyframes pulse {
          0% {
            transform: scale(1);
          }
          50% {
            transform: scale(1.2);
          }
          100% {
            transform: scale(1);
          }
        }
        
        @keyframes wave {
          0% {
            transform: translateX(0) translateZ(0) scaleY(1);
          }
          50% {
            transform: translateX(-25%) translateZ(0) scaleY(0.9);
          }
          100% {
            transform: translateX(-50%) translateZ(0) scaleY(1);
          }
        }
        
        @keyframes buttonPulse {
          0% {
            box-shadow: 0 0 0 0 rgba(247, 37, 133, 0.4);
          }
          70% {
            box-shadow: 0 0 0 10px rgba(247, 37, 133, 0);
          }
          100% {
            box-shadow: 0 0 0 0 rgba(247, 37, 133, 0);
          }
        }
      `}</style>
    </Box>
  );
};

export default LandingPage;
