import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Paper, 
  Card, 
  CardContent,
  List,
  ListItem,
  ListItemText,
  Divider,
  Button,
  CircularProgress,
  useTheme,
  Container,
  Alert,
  Avatar,
  IconButton,
  LinearProgress,
  Tooltip
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import ChatIcon from '@mui/icons-material/Chat';
import HistoryIcon from '@mui/icons-material/History';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import FeedbackIcon from '@mui/icons-material/Feedback';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import AssessmentIcon from '@mui/icons-material/Assessment';
import QuestionAnswerIcon from '@mui/icons-material/QuestionAnswer';
import VerifiedIcon from '@mui/icons-material/Verified';
import api from '../services/api';

const recentQueries = [
  { id: 1, text: "What are the penalties for Section 302 of IPC?", timestamp: "2023-10-25T14:30:00" },
  { id: 2, text: "How to file an RTI application?", timestamp: "2023-10-24T11:15:00" },
  { id: 3, text: "What are my rights if I'm arrested?", timestamp: "2023-10-23T09:45:00" },
];

const popularTopics = [
  { id: 1, name: "Criminal Law", count: 245 },
  { id: 2, name: "Right to Information", count: 187 },
  { id: 3, name: "Labor Laws", count: 156 },
  { id: 4, name: "Family Law", count: 134 },
  { id: 5, name: "Property Law", count: 112 },
];

const Dashboard = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);
  const [stats, setStats] = useState({
    totalQueries: 0,
    answeredQueries: 0,
    averageConfidence: 0,
  });

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    const storedToken = localStorage.getItem('token');

    if (!storedToken || !storedUser) {
      navigate('/login', { state: { from: { pathname: '/dashboard' } } });
      return;
    }

    try {
      setUser(JSON.parse(storedUser));
    } catch (err) {
      console.error("Failed to parse user data", err);
      setError("Session data is corrupted. Please log in again.");
      localStorage.removeItem('user');
      localStorage.removeItem('token');
      navigate('/login');
      return;
    }

    const timer = setTimeout(() => {
      try {
        setStats({
          totalQueries: 32,
          answeredQueries: 28,
          averageConfidence: 86.5,
        });
        setLoading(false);
      } catch (err) {
        console.error("Error loading dashboard data", err);
        setError("Failed to load dashboard data. Please try again.");
        setLoading(false);
      }
    }, 1000);

    return () => clearTimeout(timer);
  }, [navigate]);

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
        <Button 
          variant="contained" 
          onClick={() => navigate('/')}
          sx={{ mt: 2 }}
        >
          Return to Home
        </Button>
      </Container>
    );
  }

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      pt: 4, 
      pb: 8,
      backgroundColor: theme.palette.mode === 'dark' ? 'rgba(0,0,0,0.04)' : 'rgba(67, 97, 238, 0.03)'
    }}>
      <Container maxWidth="lg">
        <Box sx={{ 
          mb: 5,
          display: 'flex',
          flexDirection: { xs: 'column', sm: 'row' },
          justifyContent: 'space-between',
          alignItems: { xs: 'flex-start', sm: 'center' }
        }}>
          <Box>
            <Typography variant="h4" fontWeight="bold" gutterBottom sx={{ 
              background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent' 
            }}>
              Welcome to Your Dashboard
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: { xs: 2, sm: 0 } }}>
              {user ? `Hello, ${user.name || user.email} ðŸ‘‹` : 'Overview of your legal information'}
            </Typography>
          </Box>
          <Button 
            variant="contained" 
            component={Link}
            to="/chat"
            startIcon={<ChatIcon />}
            sx={{ 
              borderRadius: 8,
              px: 3,
              py: 1,
              background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.primary.dark})`,
              boxShadow: '0 8px 16px rgba(67, 97, 238, 0.2)',
              transition: 'all 0.3s ease',
              '&:hover': {
                transform: 'translateY(-3px)',
                boxShadow: '0 12px 20px rgba(67, 97, 238, 0.3)',
              }
            }}
          >
            Start New Query
          </Button>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress />
          </Box>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12} md={4}>
              <Card sx={{ 
                borderRadius: 3, 
                overflow: 'hidden',
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 12px 30px rgba(0,0,0,0.08)'
                }
              }}>
                <Box sx={{
                  display: 'flex', 
                  alignItems: 'center',
                  p: 3,
                  background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.primary.light})`,
                  color: 'white'
                }}>
                  <Avatar sx={{ 
                    bgcolor: 'rgba(255,255,255,0.2)', 
                    width: 56, 
                    height: 56,
                    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                    mr: 2
                  }}>
                    <AssessmentIcon fontSize="large" />
                  </Avatar>
                  <Box>
                    <Typography variant="body2" sx={{ opacity: 0.8, fontWeight: 500 }}>
                      Total Queries
                    </Typography>
                    <Typography variant="h4" fontWeight="bold">
                      {stats.totalQueries}
                    </Typography>
                  </Box>
                </Box>
                <CardContent sx={{ p: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                    <Typography variant="body2" color="text.secondary">
                      {stats.totalQueries > 0 ? '32% increase from last month' : 'No queries yet'}
                    </Typography>
                    <Tooltip title="View Details">
                      <IconButton size="small">
                        <MoreVertIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <Card sx={{ 
                borderRadius: 3, 
                overflow: 'hidden',
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 12px 30px rgba(0,0,0,0.08)'
                }
              }}>
                <Box sx={{
                  display: 'flex', 
                  alignItems: 'center',
                  p: 3,
                  background: `linear-gradient(45deg, ${theme.palette.secondary.main}, ${theme.palette.secondary.light})`,
                  color: 'white'
                }}>
                  <Avatar sx={{ 
                    bgcolor: 'rgba(255,255,255,0.2)', 
                    width: 56, 
                    height: 56,
                    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                    mr: 2
                  }}>
                    <QuestionAnswerIcon fontSize="large" />
                  </Avatar>
                  <Box>
                    <Typography variant="body2" sx={{ opacity: 0.8, fontWeight: 500 }}>
                      Answered Queries
                    </Typography>
                    <Typography variant="h4" fontWeight="bold">
                      {stats.answeredQueries}
                    </Typography>
                  </Box>
                </Box>
                <CardContent sx={{ p: 3 }}>
                  <Box sx={{ mt: 1, mb: 2 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                      <Typography variant="body2" color="text.secondary">Completion</Typography>
                      <Typography variant="body2" fontWeight="bold">
                        {Math.round((stats.answeredQueries / stats.totalQueries) * 100)}%
                      </Typography>
                    </Box>
                    <LinearProgress 
                      variant="determinate" 
                      value={(stats.answeredQueries / stats.totalQueries) * 100}
                      sx={{ 
                        height: 6, 
                        borderRadius: 3,
                        bgcolor: 'rgba(0,0,0,0.05)',
                        '& .MuiLinearProgress-bar': {
                          bgcolor: theme.palette.secondary.main
                        }
                      }}
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
            
            <Grid item xs={12} md={4}>
              <Card sx={{ 
                borderRadius: 3, 
                overflow: 'hidden',
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 12px 30px rgba(0,0,0,0.08)'
                }
              }}>
                <Box sx={{
                  display: 'flex', 
                  alignItems: 'center',
                  p: 3,
                  background: 'linear-gradient(45deg, #2196f3, #21CBF3)',
                  color: 'white'
                }}>
                  <Avatar sx={{ 
                    bgcolor: 'rgba(255,255,255,0.2)', 
                    width: 56, 
                    height: 56,
                    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                    mr: 2
                  }}>
                    <VerifiedIcon fontSize="large" />
                  </Avatar>
                  <Box>
                    <Typography variant="body2" sx={{ opacity: 0.8, fontWeight: 500 }}>
                      Average Confidence
                    </Typography>
                    <Typography variant="h4" fontWeight="bold">
                      {stats.averageConfidence}%
                    </Typography>
                  </Box>
                </Box>
                <CardContent sx={{ p: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Box sx={{ 
                      width: 12, 
                      height: 12, 
                      borderRadius: '50%', 
                      bgcolor: stats.averageConfidence > 80 ? 'success.main' : 'warning.main',
                      mr: 1 
                    }} />
                    <Typography variant="body2" color="text.secondary">
                      {stats.averageConfidence > 80 ? 'Excellent accuracy' : 'Good accuracy'}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Paper sx={{ 
                p: 0, 
                height: '100%', 
                borderRadius: 3,
                overflow: 'hidden',
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)'
              }}>
                <Box sx={{ 
                  p: 3, 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  borderBottom: '1px solid',
                  borderColor: 'divider'
                }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Avatar sx={{ 
                      bgcolor: theme.palette.primary.main,
                      width: 40,
                      height: 40,
                      mr: 2
                    }}>
                      <HistoryIcon />
                    </Avatar>
                    <Typography variant="h6" fontWeight="bold">
                      Recent Queries
                    </Typography>
                  </Box>
                  <Button 
                    component={Link} 
                    to="/history" 
                    startIcon={<HistoryIcon />}
                    size="small"
                    sx={{ borderRadius: 2 }}
                  >
                    View All
                  </Button>
                </Box>
                <List>
                  {recentQueries.map((query, index) => (
                    <React.Fragment key={query.id}>
                      <ListItem 
                        alignItems="flex-start" 
                        sx={{ 
                          px: 3, 
                          transition: 'background-color 0.2s',
                          '&:hover': {
                            bgcolor: 'rgba(0,0,0,0.02)'
                          }
                        }}
                      >
                        <ListItemText
                          primary={
                            <Typography variant="body1" fontWeight={500}>
                              {query.text}
                            </Typography>
                          }
                          secondary={
                            <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                              {new Date(query.timestamp).toLocaleString()}
                            </Typography>
                          }
                        />
                      </ListItem>
                      {index < recentQueries.length - 1 && <Divider component="li" />}
                    </React.Fragment>
                  ))}
                </List>
                {recentQueries.length === 0 && (
                  <Box sx={{ textAlign: 'center', py: 4 }}>
                    <Typography color="text.secondary">No recent queries</Typography>
                  </Box>
                )}
              </Paper>
            </Grid>

            <Grid item xs={12} md={6}>
              <Paper sx={{ 
                p: 0, 
                height: '100%', 
                borderRadius: 3,
                overflow: 'hidden',
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)'
              }}>
                <Box sx={{ 
                  p: 3, 
                  borderBottom: '1px solid',
                  borderColor: 'divider',
                  display: 'flex', 
                  alignItems: 'center'
                }}>
                  <Avatar sx={{ 
                    bgcolor: theme.palette.secondary.main,
                    width: 40,
                    height: 40,
                    mr: 2
                  }}>
                    <TrendingUpIcon />
                  </Avatar>
                  <Typography variant="h6" fontWeight="bold">
                    Popular Legal Topics
                  </Typography>
                </Box>
                <List>
                  {popularTopics.map((topic, index) => (
                    <React.Fragment key={topic.id}>
                      <ListItem 
                        sx={{ 
                          px: 3,
                          transition: 'background-color 0.2s',
                          '&:hover': {
                            bgcolor: 'rgba(0,0,0,0.02)'
                          }
                        }}
                      >
                        <Box sx={{ width: '100%' }}>
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                            <Typography variant="body1" fontWeight={500}>{topic.name}</Typography>
                            <Typography variant="body2" color="text.secondary">
                              {topic.count} queries
                            </Typography>
                          </Box>
                          <LinearProgress 
                            variant="determinate" 
                            value={(topic.count / 245) * 100}
                            sx={{ 
                              height: 4, 
                              borderRadius: 2,
                              bgcolor: 'rgba(0,0,0,0.05)',
                              '& .MuiLinearProgress-bar': {
                                bgcolor: index % 2 === 0 ? theme.palette.primary.main : theme.palette.secondary.main
                              }
                            }}
                          />
                        </Box>
                      </ListItem>
                      {index < popularTopics.length - 1 && <Divider component="li" />}
                    </React.Fragment>
                  ))}
                </List>
              </Paper>
            </Grid>

            <Grid item xs={12}>
              <Paper sx={{ 
                p: 4, 
                borderRadius: 3,
                boxShadow: '0 8px 20px rgba(0,0,0,0.05)'
              }}>
                <Typography variant="h6" fontWeight="bold" gutterBottom>
                  Quick Actions
                </Typography>
                <Grid container spacing={3} sx={{ mt: 1 }}>
                  {[
                    { 
                      icon: <ChatIcon />, 
                      label: "New Query", 
                      color: theme.palette.primary.main,
                      link: "/chat",
                      variant: "contained"
                    },
                    { 
                      icon: <HistoryIcon />, 
                      label: "View History", 
                      color: theme.palette.secondary.main,
                      link: "/history", 
                      variant: "outlined"
                    },
                    { 
                      icon: <TrendingUpIcon />, 
                      label: "Analytics", 
                      color: "#2196f3",
                      variant: "outlined" 
                    },
                    { 
                      icon: <FeedbackIcon />, 
                      label: "Send Feedback", 
                      color: "#9c27b0",
                      variant: "outlined" 
                    },
                  ].map((action, index) => (
                    <Grid item xs={12} sm={6} md={3} key={index}>
                      <Button
                        variant={action.variant}
                        fullWidth
                        component={action.link ? Link : Button}
                        to={action.link}
                        startIcon={action.icon}
                        sx={{ 
                          py: 2,
                          px: 3,
                          borderRadius: 2,
                          borderColor: action.color,
                          color: action.variant === 'outlined' ? action.color : 'white',
                          bgcolor: action.variant === 'contained' ? action.color : 'transparent',
                          '&:hover': {
                            bgcolor: action.variant === 'contained' ? action.color : `${action.color}10`,
                            borderColor: action.color
                          }
                        }}
                      >
                        {action.label}
                      </Button>
                    </Grid>
                  ))}
                </Grid>
              </Paper>
            </Grid>
          </Grid>
        )}
      </Container>
    </Box>
  );
};

export default Dashboard;
